export const isZero = (value) => value === 0;
export const isNotZero = (value) => value !== 0;
export const isPropertyZero = (property) => (value) => value[property] === 0;
export const isPropertyNotZero = (property) => (value) => value[property] !== 0;
export const arePropertiesZero = (...properties) => (value) => properties.every((property) => value[property] === 0);
export const arePropertiesNotZero = (...properties) => (value) => properties.every((property) => value[property] !== 0);
